import React, { Component } from 'react';
 import { Card, CardImg, CardBody, CardText, CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
 import {Link} from 'react-router-dom'


     function renderComments(comments) {
         if (comments == null) {
             return (<div></div>)
        }
        const cmnts = comments.map(comment => {
             return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                     <p>-- {comment.author},
                    &nbsp;
                    {new Intl.DateTimeFormat('en-IN', {
                            year: 'numeric',
                            month: 'long',
                            day: '2-digit'
                        }).format(new Date(comment.date))}
                    </p>
                </li>
             )
        })
         return (
             <div className='col-12 col-md-5 m-1'>
                <h4> Comments </h4>
                <ul className='list-unstyled'>
                    {cmnts}
                </ul>

            </div>
        )
    }

    function renderProd(prod) {
        if (prod != null) {
            return (
               <div className='col-12 col-md-5 m-1'>
                     <Card>
                        <CardImg width="100%" src={prod.image} alt={prod.name} />
                        <CardBody>
                            <CardTitle>{prod.name}</CardTitle>
                           <CardText>{prod.description}</CardText>
                       </CardBody>
                   </Card>
               </div>
             )
         }
         else {
             return (<div></div>)
         }
     }

     const Productdetail = (props) => {
         const prod = props.prod
         if (prod == null) {
          return (<div></div>)
         }
         const prodItem = renderProd(prod)
         const commentItem = renderComments(props.comments)
         return (
         <div className="container">
       	<div className="row">
            	<Breadcrumb>
             		<BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
             		<BreadcrumbItem active>{props.prod.name}</BreadcrumbItem>
             	</Breadcrumb>
             	<div className='col-12'>
 	            	<h3>{props.prod.name}</h3>
 	            	<hr />
             	</div>
             </div>
             <div className='row'>
                 {prodItem}
                 {commentItem}
             </div>
         </div>
         )
     }


export default Productdetail
