import React, { Component } from 'react';
import { Navbar, NavbarBrand } from 'reactstrap';
import Menu from './MenuComponent';
import {PRODUCTS} from '../shared/products';
import Productdetail from './ProductdetailComponent';


class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: PRODUCTS,
      selectedProd:null
    };
  }

  onProdSelect(prodId){
    this.setState({selectedProd:prodId});
  }


render() {
  return (
    <div>
        <Navbar dark color="secondary">
          <div className="container">
            <NavbarBrand href="/">My Grocery Store.Com</NavbarBrand>
          </div>
        </Navbar>
        <Menu products={this.state.products}
        onClick={(prodId) => this.onProdSelect(prodId)}/>
        <Productdetail prod={this.state.products.filter((prod) => prod.id === this.state.selectedProd)[0]}/>
    </div>
  );
}
}

export default Main;
